// import logo from './logo.svg';
import './App.css';
import Home from './components/Home';
import About from './components/About';
import Contacts from './components/Contacts';
import News from './components/News';
import {BrowserRouter, Routes, Route, NavLink} from 'react-router-dom';
function App() {
  return (
    <BrowserRouter>
      <div className="wrapper">
        <nav className="menu">
          <NavLink className={({isActive}) => isActive ? "menuLinkActive" : ""} to="/">Main</NavLink>
          <NavLink className={({isActive}) => isActive ? "menuLinkActive" : ""} to="/post">P</NavLink>

          <NavLink className={({isActive}) => isActive ? "menuLinkActive" : ""} to="/login">About</NavLink>

        </nav>
        <Routes>
        <Route path='/' element={<Main />} />
          <Route path='/post' element={<Posts/>} />
          <Route path='/login' element={<Login/>} />

        </Routes>
      </div>
    </BrowserRouter>)}  
export default App;
